import React from 'react'
import { useDispatch } from 'react-redux'
import { editMessages } from 'reducers/messages';
import { Button } from '@material-ui/core';
import styled from 'styled-components'


export const EditMessage = ({ id, author, message }) => {
  const dispatch = useDispatch();

  const handleEdit = (event) => {
    event.preventDefault()
    dispatch(updateGuests(formValues, guest))
    dispatch(editMessages({ id, author, message, newValue }))
  }

  return (
    <div>
      <Button variant="contained" type="submit" onClick={handleEdit}>
        Edit
        </Button>
      <TextField
        className="input-field"
        label="Edit comment"
        id="outlined-multiline-static"
        multiline
        rows={2}
        variant="outlined"
        type="message"
        required
        value={newValue}
        onChange={event => setMessage(event.target.value)}
      />
    </div>
  )
}
