import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { editMessages } from 'reducers/messages';
import IconButton from '@material-ui/core/IconButton';
import EditIcon from '@material-ui/icons/Edit';
import styled from 'styled-components'
import EdiText from "react-editext";


const Main = styled.div`
padding: 10px;
display: flex;
flex-direction: row;
/* justify-content: center;
align-items: center; */
`
const StyledEdiText = styled(EdiText)`
.message-text {
}
  /* button {
    border-radius: 5px;
  }
  button[editext="edit-button"] {
    color: #000;
    width: 50px;
  }
  button[editext="save-button"] {
    width: 50px;
    &:hover {
      background: greenyellow;
    }
  }
  button[editext="cancel-button"] {
    &:hover {
      background: crimson;
      color: #fff;
    }
  }
  input, textarea {
    background: #1D2225;
    color: #F4C361;
    font-weight: bold;
    border-radius: 5px;
  }
  div[editext="view-container"], div[editext="edit-container"] {
    background: #6293C3;
    padding: 15px;
    border-radius: 5px;
    color: #fff;
} */
`

export const EditMessage = ({ id, author, message }) => {
  const [newValue, setNewValue] = useState()
  const [editing, setEditing] = useState(false);

  const dispatch = useDispatch();

  const handleEdit = (newValue) => {
    setNewValue(newValue)
    dispatch(editMessages({ id, author, newValue }))
  }

  return (
    <Main>
      <StyledEdiText
        value={message}
        validationMessage="Please type at least 1 characters."
        validation={val => val.length > 0}
        type="textarea"
        buttonsAlign='before'
        onSave={handleEdit}
        editing={editing}
        showButtonsOnHover
        viewProps={{
          className: 'message-text',
          style: { borderRadius: 3 }
        }}
        inputProps={{
          className: 'textarea',
          placeholder: 'Type your content here',
          style: {
            outline: 'none',
            minWidth: 'auto'
          },
          rows: 5
        }}
      // editButtonContent={<EditIcon fontSize="small" />}
      // saveButtonContent="Save"
      // cancelButtonContent="Cancel"
      // hideIcons={true}
      />
      {/* 
      <IconButton aria-label="edit" onClick={() => setEditing(!editing)}>
        <EditIcon />
      </IconButton> */}

      {/* <Button variant="contained" onClick={() => setEditing(!editing)}>Edit</Button> */}

      {/* <EdiText
        value={message}
        type="textarea"
        inputProps={{ rows: 5 }}
        onSave={handleEdit}
        editing={editing}
        hideIcons={true}
        saveButtonContent="Save"
        cancelButtonContent="Cancel"
        editButtonContent="Edit message"
      /> */}
    </Main>
  )
}
