import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  messages: []
}


export const messages = createSlice({
  name: 'messages',
  initialState,
  reducers: {
    setMessages: (state, action) => {
      state.messages = action.payload
    }
  }
})

//GET ALL MESSAGES
export const fetchMessages = () => {
  return dispatch => {
    // dispatch(ui.actions.setLoading(true))
    fetch(`https://22a38f59-54b8-43e6-86ca-080ccfd450a7.mock.pstmn.io/messages`)
      .then(res => res.json())
      .then(json => {
        dispatch(messages.actions.setMessages(json.results))
        // dispatch(ui.actions.setLoading(false))
      })
  }
}
