import React, { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { postMessages } from 'reducers/messages';
import { Card, Button, Input } from '@material-ui/core';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import styled from 'styled-components'

// const Form = styled.div`
// display: flex;
// flex-direction:column;
// `
// const Card = styled.div`
// display: flex;
// flex-direction:column;
// color: white;
// background: lightgoldenrodyellow;
// `
const Rotate = styled.div`
& {
  .expand {
    transform: rotate(0deg);
    margin-left: auto;
  }
  .expandOpen {
    transform: rotate(180deg);
  }
}
`
export const PostMessage = () => {
  const [message, setMessage] = useState("")
  const [author, setAuthor] = useState("")
  const [expanded, setExpanded] = useState(false)

  const dispatch = useDispatch();

  // useEffect(() => {
  //   dispatch(postMessages());
  // }, [dispatch]);

  const handleSubmit = (event) => {
    event.preventDefault()
    dispatch(postMessages({ message, author }))
  }

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };


  return (
    <Card>
      <CardHeader
        avatar={
          <Avatar aria-label="recipe">
            R
          </Avatar>
        }
        title="Shrimp and Chorizo Paella"
        subheader="September 14, 2016"
      />

      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          New message
       <input
            type="message"
            required
            value={message}
            onChange={event => setMessage(event.target.value)}
          />
        </Typography>
        <Typography variant="body2" color="textSecondary">
          Author
      <input
            type="author"
            required
            value={author}
            onChange={event => setAuthor(event.target.value)}
          />
        </Typography>
        <Button type="submit" onClick={handleSubmit}>
          Post
        </Button>
      </CardContent>
      <Rotate>
        <IconButton
          className={expanded ? "expandOpen" : "expand"}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </IconButton>
      </Rotate>

      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Replies:</Typography>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and
            set aside for 10 minutes.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>

    // <CardContent>
    //   Message
    //   <Input
    //     type="message"
    //     required
    //     value={message}
    //     onChange={event => setMessage(event.target.value)}
    //   />
    //       Author
    //   <Input
    //     type="author"
    //     required
    //     value={author}
    //     onChange={event => setAuthor(event.target.value)}
    //   />
    //   <Button type="submit" onClick={handleSubmit}>
    //     Post
    //     </Button>
    // </CardContent>
    // </Card >
  )
}
