import React, { useState } from 'react'
import styled from 'styled-components/macro'
import { Registration } from 'components/Registration'
import { Login } from 'components/Login'
import { useSelector } from 'react-redux'
import { Logout } from './Logout'
import { Button } from '@material-ui/core'



const Main = styled.div`
background: lightgoldenrodyellow;
`
const HeaderContent = styled.div`
background: lightgoldenrodyellow;
`

export const Header = () => {
  const [showRegistration, setRegistrationShow] = useState(false)
  const [showLogin, setLoginShow] = useState(false)

  const accessToken = useSelector((state) => state.users.accessToken)

  const handleClick = () => {
    setLoginShow(!showLogin)
    setRegistrationShow(!showRegistration)

  }
  return (
    <Main>
      <HeaderContent>
        HALLÅÅÅÅ
      </HeaderContent>
      {!accessToken &&
        <>
          <div>
            <Button onClick={() => setRegistrationShow(!showRegistration)}>Register</Button>
            <Button onClick={() => setLoginShow(!showLogin)}>Login</Button>
          </div>

          <>
            {showRegistration && (
              <Registration handleClick={handleClick} />
            )
            }
            {showLogin && (
              <Login handleClick={handleClick} />
            )}
          </>
        </>
      }
      {accessToken &&
        <Logout />
      }
    </Main >
  )
}
