import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { createUser } from 'reducers/users';

import { Button, Form, div, Input, label } from '@material-ui/core'

const url = "https://final-movie-match.herokuapp.com/users"


export const Registration = () => {
  const [userName, setName] = useState("")
  const [password, setPassword] = useState("")
  const [registred, setRegistred] = useState(false)
  const [failure, setFailure] = useState(false)
  // const history = useHistory()

  const dispatch = useDispatch();

  const handleSubmit = (event) => {
    event.preventDefault()
    dispatch(createUser({ userName, password }))
    //Clear inputfield
  }


  const reDirect = () => {
    // history.push(`/login`)
  }

  return (
    <div>
      {registred &&
        <div color={"#fff"}>Success! Continuing to login...</div>
      }
      {!registred && (
        <div>
          <form onSubmit={handleSubmit}>
            {!failure && <div>CREATE NEW USER</div>}
            {failure && (
              <div>
                User not created. Try using another user name
              </div>
            )}
            <label>
              Name {userName.length < 2 && userName.length !== 0 && " is too short"}
              {userName.length > 20 && " is too long"}
              <Input
                type="text"
                required
                value={userName}
                onChange={event => setName(event.target.value)}
              ></Input>
            </label>

            <label>
              Password{" "}
              {password.length < 5 && password.length !== 0 && " is too short"}
              <Input
                type="password"
                required
                value={password}
                onChange={event => setPassword(event.target.value)}
              ></Input>
            </label>
            <Button
              type="submit"
              disabled={
                userName.length > 1 &&
                  userName.length < 21 &&
                  password.length > 4 &&
                  userName
                  ? false
                  : true
              }
              onClick={handleSubmit}
            >
              SIGN UP
            </Button>
            <Button type="button" onClick={reDirect}>
              Already signed up?
            </Button>
          </form>
        </div>
      )}
    </div>
  )
}
