import React from 'react'
import { useSelector } from 'react-redux'
import styled from 'styled-components'

const MessageBox = styled.div`
background: red;
`
const Chip = styled.div`
  display: inline-flex;
  background: #f1f1f1;
  border: palevioletred solid 1px;
  color: #707070;
  font-size: 14px;
  border-radius: 15px;
  padding: 5px 10px;
  margin: 5px;
  `

export const MessageBoard = () => {
  const post = useSelector((state) => state.messages.message)
  console.log(useSelector((state) => state.messages))

  return (
    <MessageBox>
      {post.message.map((message) =>
        <Chip>#{message}</Chip>)}
    </MessageBox>
  )
}
