import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { deleteMessages } from 'reducers/messages';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import styled from 'styled-components/macro'


export const DeleteMessage = ({ id, author }) => {
  const dispatch = useDispatch();
  const error = useSelector(state => state.ui.setErrorMessage)

  const handleRemove = (event) => {
    event.preventDefault()
    console.log(id, author)
    dispatch(deleteMessages({ id, author }))
  }

  return (
    <div>
      {/* {error &&
        <div>not your message</div>
      } */}
      <IconButton aria-label="delete" disabled={error} onClick={handleRemove}>
        <DeleteIcon />
      </IconButton>
    </div>
  )
}
