import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { postMessages, fetchMessages } from 'reducers/messages';
import { TextField, Button, Card } from '@material-ui/core';
import styled from 'styled-components/macro'

const Main = styled.div`
  /* padding: 10px;
  margin-top: 10px;
  display: flex;
  flex-direction:column; */
  & {
.postMessage {
  background: #f1f1f1;
  margin: 10px;
  width: 400px;
  }
.avatar {
  background: black;
  }
.input-field {
  width: 100%;
  margin-bottom: 5px;
  }
}
`


export const PostReply = ({ parentId }) => {
  const [message, setMessage] = useState("")
  const dispatch = useDispatch();

  const author = useSelector((state) => state.users.userId)


  const handleSubmit = (event) => {
    event.preventDefault()
    dispatch(postMessages({ message, author, parentId }))
    dispatch(fetchMessages());
    setMessage("")
  }

  return (
    <Main>
      <Card className="postMessage">
        <TextField
          className="input-field"
          label="New comment"
          id="outlined-multiline-static"
          multiline
          rows={1}
          variant="outlined"
          type="message"
          required
          value={message}
          onChange={event => setMessage(event.target.value)}
        />
        <Button size="small" variant="contained" type="submit" onClick={handleSubmit}>
          Post reply
        </Button>
      </Card>
    </Main>
  )
}
