import React, { useState } from 'react'
import { Button, Input } from '@material-ui/core'
import styled from 'styled-components'

const url = "https://final-movie-match.herokuapp.com/users"


export const Registration = () => {
  const [userName, setUserName] = useState("")
  const [password, setPassword] = useState("")
  const [registred, setRegistred] = useState(false)
  const [failure, setFailure] = useState(false)

  const handleSubmit = event => {
    event.preventDefault();
    fetch(url, {
      method: "POST",
      body: JSON.stringify({ userName, password }),
      headers: { "Content-Type": "application/json" }
    })
      .then(res => {
        if (res.status !== 201) {
          return (
            res.json().then(json => console.log(json.message)), setFailure(true)
          )
        } else {
          setRegistred(true)
          setTimeout(reDirect, 2000);
        }
      })
      .catch(err => console.log("Error:", err))
  }

  const reDirect = () => {
    // history.push(`/login`)
  }

  return (
    <div>
      {registred &&
        <div color={"#fff"}>Success! Continuing to login...</div>
      }
      {!registred && (
        <div>
          <form onSubmit={handleSubmit}>
            {!failure && <div>CREATE NEW USER</div>}
            {failure && (
              <div>
                User not created. Try using another name or email!
              </div>
            )}
            <label>
              Name {userName.length < 2 && userName.length !== 0 && " is too short"}
              {userName.length > 20 && " is too long"}
              <Input
                type="text"
                required
                value={userName}
                onChange={event => setUserName(event.target.value)}
              ></Input>
            </label>

            <label>
              Password{" "}
              {password.length < 5 && password.length !== 0 && " is too short"}
              <Input
                type="password"
                required
                value={password}
                onChange={event => setPassword(event.target.value)}
              ></Input>
            </label>
            <Button
              type="submit"
              disabled={
                userName.length > 1 &&
                  userName.length < 21 &&
                  password.length > 4 &&
                  userName
                  ? false
                  : true
              }
              onClick={handleSubmit}
            >
              SIGN UP
            </Button>
            <Button type="button" onClick={reDirect}>
              Already a member?
            </Button>
          </form>
        </div>
      )}
    </div>
  )
}
