        />
        <CardContent>
          <Typography variant="body2" color="textSecondary" component="div">
            Message: {message.message}
          </Typography>
          <PostReply parentId={message._id} />
          <ToggleComments parentId={message._id} />
        </CardContent>
        <Rotate>
          <IconButton
            className={expanded ? "expandOpen" : "expand"}
