import React, { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { fetchMessages } from 'reducers/messages';
import { Card } from '@material-ui/core';
import styled from 'styled-components'
import { PostMessage } from './PostMessage';
import { ReplyMessage } from './ReplyMessage';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

// const MessageBox = styled.div`
// display:flex;
// flex-direction: column;
// align-items:center;
// & {
// .messageCard {
//   background: lightsalmon;
//   margin: 10px;
//   padding: 10px;
//   color: red;
//   width: 400px;
// }
// }
// `
const Rotate = styled.div`
& {
  .expand {
    transform: rotate(0deg);
    margin-left: auto;
  }
  .expandOpen {
    transform: rotate(180deg);
  }
}
`
// const Card = styled.div`
//  background: green;
//  margin: 10px;
//   `


export const ShowMessages = () => {
  const [expanded, setExpanded] = useState(false)
  const dispatch = useDispatch();

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  useEffect(() => {
    dispatch(fetchMessages());
  }, [dispatch]);

  const messages = useSelector((state) => state.messages.messages)
  console.log(messages)

  return (
    <>
      {messages.map((message) => (
        <Card key={message._id}>
          <CardHeader
            avatar={
              <Avatar aria-label="recipe">
                R
          </Avatar>
            }
            title={message.author}
            subheader={message.createdAt}
          />
          <CardContent>
            <Typography variant="body2" color="textSecondary" component="p">
              <div>Message: {message.message}</div>
              <div>Author: {message.author}</div>
              <div>Time: {message.createdAt}</div>
              {/* <ReplyMessage id={message._id} /> */}
            </Typography>
          </CardContent>
          <Rotate>
            <IconButton
              className={expanded ? "expandOpen" : "expand"}
              onClick={handleExpandClick}
              aria-expanded={expanded}
              aria-label="show more"
            >
              <ExpandMoreIcon />
            </IconButton>
          </Rotate>

          <Collapse in={expanded} timeout="auto" unmountOnExit>
            <CardContent>
              <Typography paragraph>Replies:</Typography>
              <Typography paragraph>
                Heat 1/2 cup of the broth in a pot until simmering, add saffron and
                set aside for 10 minutes.
          </Typography>
            </CardContent>
          </Collapse>
        </Card>
      ))}
    </>


    // <MessageBox>
    //   <PostMessage />

    //   {messages.map((message) => (
    //     <Card className="messageCard" key={message._id}>
    //       <div>Message: {message.message}</div>
    //       <div>Author: {message.author}</div>
    //       <div>Time: {message.createdAt}</div>
    //       <ReplyMessage id={message._id} />

    //     </Card>
    //   ))}
    // </MessageBox>
  )
}
