/* display: flex;
flex-direction: column;
justify-content: center;
align-items: center; */
& {
.messageCard {
  margin: 10px;
  width: 400px;
  }
.replyContainer {
  padding: 0px;
  padding-bottom: 0;
  margin: 0px;
}
.replyCard {
  border: lightgrey solid 1px;
  }
}
`

const Rotate = styled.div`
& {
  .expand {
    transform: rotate(0deg);
    margin-left: auto;
  }
  .expandOpen {
    transform: rotate(180deg);
  }
}
`

export const MessageCard = ({ id, message, author, createdAt, onClickEdit, onClickDelete }) => {
  const [expanded, setExpanded] = useState(true)

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };


