          <CardContent className="replyContainer">
            {message.children && message.children.length && message.children.length > 0 && message.children.map((reply) => (
              <CardContent key={reply._id} className="replyCard">
                <CardHeader
                  avatar={
                    <Avatar aria-label="author">
                      R
