import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Heading, FieldContainer, Form, Label, Input, Button } from "./Styling";



export const Registration = () => {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [registred, setRegistred] = useState(false);
  const [failure, setFailure] = useState(false);
  const history = useHistory();

  const handleSubmit = event => {
    event.preventDefault();
    fetch("http://localhost:8080/users", {
      method: "POST",
      body: JSON.stringify({ userName, password }),
      headers: { "Content-Type": "application/json" }
    })
      //H채r h채nder en callback funktion
      .then(res => {
        // console.log(res.json())
        if (res.status !== 201) {
          return (
            res.json().then(json => console.log(json.message)), setFailure(true)
          );
        } else {
          setRegistred(true);
          setTimeout(reDirect, 2000);
        }
      })
      .catch(err => console.log("Error:", err));
  };

  const reDirect = () => {
    history.push(`/`);
  };

  return (
    <FieldContainer>
      {!registred && (
        <FieldContainer>
          <Form onSubmit={handleSubmit}>
            {!failure && <Heading>Create new user</Heading>}
            {failure && (
              <Heading>
                User not created. Try using another userName or email!
              </Heading>
            )}

            <Label>
              userName {userName.length < 2 && userName.length !== 0 && " is too short"}
              {userName.length > 20 && " is too long"}
              <Input
                type="text"
                required
                value={userName}
                onChange={event => setUserName(event.target.value)}
              ></Input>
            </Label>
            <Label>
              Password{" "}
              {password.length < 5 && password.length !== 0 && " is too short"}
              <Input
                type="password"
                required
                value={password}
                onChange={event => setPassword(event.target.value)}
              ></Input>
            </Label>
            <Button
              type="submit"
              disabled={
                userName.length > 1 &&
                  userName.length < 21 &&
                  password.length > 4 &&
                  userName
                  ? false
                  : true
              }
              onClick={handleSubmit}
            >
              SIGN UP
            </Button>
            <Button type="button" onClick={reDirect}>
              Already a member?
            </Button>
          </Form>
        </FieldContainer>
      )}
      {registred && (
        <FieldContainer>
          <Heading>User created!</Heading>
          <Heading>Continuing to login...</Heading>
        </FieldContainer>
      )}
    </FieldContainer>
  );
};
