import React from 'react'
import { useDispatch } from 'react-redux'
import { editMessages } from 'reducers/messages';
import { Button } from '@material-ui/core';
import styled from 'styled-components'
import EdiText from "react-editext";


export const EditMessage = ({ id, author, message }) => {
  const [newValue, setNewValue] = useState()
  const [editing, setEditing] = useState(false);

  const dispatch = useDispatch();

  const handleEdit = (event) => {
    event.preventDefault()
    setNewValue(newValue)
    dispatch(editMessages({ id, author, newValue }))
  }

  return (
    <div>
      <Button variant="contained" type="submit" onClick={handleEdit}>
        Edit
        </Button>
      <EdiText
        value={message}
        type="textarea"
        inputProps={{ rows: 5 }}
        onSave={handleEdit}
        editing={editing}
        hideIcons={true}
        saveButtonContent="Save"
        cancelButtonContent="Cancel"
        editButtonContent="Edit message"
      />
    </div>
  )
}
