import React, { useState } from 'react'
import styled from 'styled-components/macro'
import { Registration } from 'components/Registration'
import { Login } from 'components/Login'
import { useSelector } from 'react-redux'
import { Logout } from './Logout'


const Main = styled.div`
background: lightgoldenrodyellow;
`

export const Header = () => {
  const [showRegistration, setRegistrationShow] = useState(true)
  const [showLogin, setLoginShow] = useState(true)

  const accessToken = useSelector((state) => state.users.accessToken)



  return (
    <Main>

      {!accessToken &&
        <>
          {showRegistration && (
            <>
              <button onClick={() => setRegistrationShow(!showRegistration)}>Register</button>
              <Registration />
            </>)
          }
          {showRegistration && (
            <>
              <button onClick={() => setLoginShow(!showLogin)}>Login</button>
              <Login />
            </>)}
        </>
      }
      {accessToken &&
        <Logout />
      }
    </Main >
  )
}
